package project0211;

public class Second {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			System.out.println("안녕"+"하세요.\n오늘은\t" +1+"일차 수업입니다.");
			// \n은 줄바꿈 '; \t는 탭
			System.out.println("3 + 4 ="+ 3+4);
			System.out.println("3 + 4 ="+(3+4));

			//정수출력
			System.out.println(10);			// 10진수 10
			System.out.println(0x10);			// 16진수 10
			System.out.println(010);			// 8진수 10
			// 실수출력
			System.out.println(3.14f);	//float 실수
			System.out.println(3.14);		//double 실수
			System.out.println(0.314E1);	//double실수(지수표현)
	}

}
